import React from 'react';
import '../styles/cart.css';

const CartSidebar = ({ isOpen, closeCart, cart, updateQuantity, removeItem, cartTotal, openCheckout }) => {
  return (
    <div className={`cart-sidebar ${isOpen ? 'active' : ''}`}>
      <div className="cart-header">
        <h3>Your Cart</h3>
        <button className="close-cart" onClick={closeCart}>&times;</button>
      </div>
      <div className="cart-items">
        {cart.length === 0 ? (
          <p>Your cart is empty</p>
        ) : (
          cart.map(item => (
            <div className="cart-item" key={item.id}>
              <img src={item.image} alt={item.name} />
              <div className="cart-item-details">
                <p className="cart-item-name">{item.name}</p>
                <p className="cart-item-price">
                  ₹{(item.price * item.quantity).toLocaleString('en-IN')}
                </p>
                <div className="cart-item-quantity">
                  <button 
                    className="quantity-btn minus" 
                    onClick={() => updateQuantity(item.id, -1)}
                  >
                    -
                  </button>
                  <span>{item.quantity}</span>
                  <button 
                    className="quantity-btn plus" 
                    onClick={() => updateQuantity(item.id, 1)}
                  >
                    +
                  </button>
                </div>
              </div>
              <button 
                className="remove-item" 
                onClick={() => removeItem(item.id)}
              >
                <i className="fas fa-trash"></i>
              </button>
            </div>
          ))
        )}
      </div>
      <div className="cart-total">
        <p>Total: ₹<span id="cartTotal">{cartTotal.toLocaleString('en-IN')}</span></p>
        <button 
          className="checkout-btn" 
          onClick={openCheckout}
          disabled={cart.length === 0}
        >
          Proceed to Checkout
        </button>
      </div>
    </div>
  );
};

export default CartSidebar;
